import axios from 'axios';
import React, { useEffect, useState } from 'react';


const NewsItem = () => {

  const [news, setNews] = useState([]);

  useEffect(() => {
    LoadItems();
  }, [])

  const LoadItems = async () => {
    const result = await axios.get('https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=bfedf60a0901483092104a2156024f9a')
    setNews(result.data.articles)
    console.log(result.data.articles.author)
   

  }

  return (
    <>
      <div className='container'>
        <h1>Headlines</h1>
        <div className='row'>
          {
            news.map((value, index) => {
              return (
                <div className="card m-2" style={{ width: '15rem', height:'30rem' }}>
                  <img src={value.urlToImage} className="card-img-top" alt="..." />
                  <div className="card-body">
                    <h5 className="card-title">{value.title.slice(0,55)}...</h5>
                    {/* <p className="card-text">{value.description.substring(0,55)}...</p> */}
                    <a href={value.url} className="btn btn-primary">Go somewhere</a>
                  </div>
                </div>
              )
            })
          }
        </div>

      </div>
    </>
  );
}

export default NewsItem;
